import React, { Component } from 'react';
import CardList from './components/CardList';
// import axios from 'axios';

export default class App extends Component {
  state = {
    input: '',
    error: false,
    submittedInput: '',
    users: [
      { id: 1, name: 'tyroo', about: 'hi I am tyroo', age: 27 },
      { id: 2, name: 'Tansu', about: 'hi I am Tansu', age: 24 },
      { id: 3, name: 'Jonathan', about: 'hi I am Jonathan', age: 23 }
    ]
  };
  handleOnChange = event => {
    this.setState({
      input: event.target.value
    });
  };

  deleteUser = id => {
    this.setState({ users: users.filter(user => user.id !== id) });
  };

  handleSubmit = event => {
    event.preventDefault();
    this.setState({ submittedInput: this.state.input });
  };

  // componentDidMount() {
  //   axios.get('https://jsonplaceholder.typicode.com/users').then(response => {
  //     this.setState({ users: response });
  //   });
  // }

  render() {
    console.log('STATE:', this.state);
    return (
      <div className="App">
        <form action="" onSubmit={this.handleSubmit}>
          <input
            type="text"
            value={this.state.input}
            onChange={this.handleOnChange}
          />
          <input type="submit" />
        </form>

        <CardList users={this.state.users} />
      </div>
    );
  }
}
