  <Wrapper>
    <h1 className="title">Friends List</h1>

    <div>
      <h1>Score: {this.state.score} - Topscore: {this.state.topScore}</h1>
    </div>

    {friends.map(friend => (

    <FriendCard
      onclickcard={this.onclickcard}
      id={friend.id}
      name={friend.name}
      image={friend.image}
      key={friend.id}

    />
    ))}
  </Wrapper>
  )};
}

export default App;
