import React, { Component } from 'react';
import CardList from './components/CardList';
import Form from './components/Form';

export default class App extends Component {
  state = {
    formInput: '',
    users: [
      { id: 1, name: 'tyroo', about: 'hi I am tyroo', age: 27 },
      { id: 2, name: 'Tansu', about: 'hi I am Tansu', age: 24 },
      { id: 3, name: 'Jonathan', about: 'hi I am Jonathan', age: 23 }
    ]
  };
  handleFormChange = event => {
    this.setState({ formInput: event.target.value });
  };
  render() {
    console.log('STATE:', this.state);
    return (
      <div className="App">
        <Form handleInput={this.handleFormChange} />
        <CardList prop1="prop1" prop2="prop2" users={this.state.users} />
      </div>
    );
  }
}
